        public List<Location.WarpTile> warpSets = new();

        public List<Location.LocationTile> locationTiles = new();

        public Dictionary<Vector2, CharacterData.characters> dialogueTiles = new();

        public bool ambientDarkness;

        public Atoll() { }

        public Atoll(string Name)
            : base("Maps\\Shed", Name) 
        {

        }

        public override void draw(SpriteBatch b)
        {

            base.draw(b);

            foreach (LocationTile tile in locationTiles)
            {

                if (Utility.isOnScreen(tile.position, 64))
                {

                    Microsoft.Xna.Framework.Vector2 position = new(tile.position.X - (float)Game1.viewport.X, tile.position.Y - (float)Game1.viewport.Y);

                    b.Draw(Mod.instance.iconData.sheetTextures[tile.tilesheet], position, tile.rectangle, Microsoft.Xna.Framework.Color.White, 0f, Vector2.Zero, 4, SpriteEffects.None, tile.layer);

                    if (tile.shadow)
                    {
                        
                        b.Draw(Mod.instance.iconData.sheetTextures[tile.tilesheet], position + new Vector2(1,8), tile.rectangle, Microsoft.Xna.Framework.Color.Black *0.35f, 0f, Vector2.Zero, 4, SpriteEffects.None, tile.layer - 0.001f);

                    }

                }

            }

        }

        protected override void _updateAmbientLighting()
        {

            if (ambientDarkness)
            {

                if(Game1.player.currentLocation.Name != Name)
                {

                    ambientDarkness = false;

                }
                else
                {

                    Game1.ambientLight = new(192, 192, 160);

                }

                return;

            }

            base._updateAmbientLighting();

        }

