        public override void updateWarps()
        {
            //warps.Clear();

            if (warpSets.Count > 0)
            {

                warps.Clear();

                foreach (WarpTile warpSet in warpSets)
                {

                    warps.Add(new Warp(warpSet.enterX, warpSet.enterY, warpSet.location, warpSet.exitX, warpSet.exitY, flipFarmer: false));

                }

                return;

            }

            Beach beach = Game1.getLocationFromName("Beach") as Beach;

            beach.warps.Add(new Warp(93, 8, LocationData.druid_atoll_name, 14, 10, flipFarmer: false));

            beach.warps.Add(new Warp(93, 9, LocationData.druid_atoll_name, 14, 10, flipFarmer: false));

